import React from 'react'
import '../styles/CharityEvents.css';

export default class CharityEvents extends React.Component{

    state = {
        display: false
    }

    showDescription = e => {
        if(this.state.display){
            this.setState({
                display: false
            })
        }else{
            this.setState({
                display: true
            })
        }
    }

    render(){
        return(
            <div id="moreInfoTable" onClick={this.showDescription}>
                <table width="100%" border="0" cellSpacing="0" cellPadding="10" align="center">
                    <tbody>
                        <tr>
                            <td className="eventLabel" width="50%" align="left"><b>DATE:</b>&nbsp;{this.props.event_date}</td>
                            <td className="eventLabel" width="50%" align="left"><b>TYPE:</b>&nbsp;{this.props.event_type}</td>
                        </tr>
                        <tr>
                            <td className="eventLabel" width="100%" colSpan="2" align="left"><b>TITLE:</b>&nbsp;{this.props.event_title}</td>
                        </tr>
                        <tr className="eventDescription" style={{display: this.state.display ? "block" : "none"}}>
                            <td className="eventLabel" width="100%" colSpan="2" align="left"><b>DESCRIPTION:</b>&nbsp;{this.props.event_description}</td>
                        </tr>
                    </tbody>
                </table>
            </div>

        )
    }
}
