import React from 'react'

export default class DonationBar extends React.Component {

    calcBarWidth = (num) => {
        const maxAmount = 2000000
        const percentage = num/maxAmount
        return percentage
    }

    render(){
        return(
            <div id="barContainer">
                <div className="donationBar" style={{width: `${this.calcBarWidth(this.props.donation_amount)}%`}}>
                    ${this.props.donation_amount}
                    {this.calcBarWidth(this.props.donation_amount)}
                </div>
            </div>
        )
    }
}
