import React from 'react'
import '../styles/EventForm.css';

export default class Eventform extends React.Component{
    render(){
        return(
            <div id="eventForm" className="dropdownAnimation" style={{display: this.props.display ? "inline-block" : "none"}}>
                <div className="eventForm-half">
                    DATE:&nbsp;<input type="date"></input>
                </div>
                <div className="eventForm-half">
                    TYPE:&nbsp;
                        <select>
                            <option default>Select a type...</option>
                            <option>EVENT</option>
                            <option>OPPORTUNITY</option>
                        </select>
                </div>
                <div className="eventForm-full">
                    TITLE:&nbsp;<input type="text"></input>
                </div>
            </div>
        )
    }
}
