import React from 'react'

export default class Messages extends React.Component{

    state = {
        // senders: [],
        msgWindow: false
    }

    showMsgWindow = e => {
        if(this.state.msgWindow){
            this.setState({
                msgWindow: false
            })
        }else{
            this.setState({
                msgWindow: true
            })
        }
    }

    // msgSender = () =>{
