import React from 'react';
import { LoginPage, HomePage, Nav, UserProfile } from './components';
import { Route, Switch } from 'react-router-dom';
import './styles/App.css';
import './styles/HomePage.css';

const charityAPI = 'http://localhost:3000/charities'
const userAPI = 'http://localhost:3000/users'
const donationAPI = 'http://localhost:3000/donations'

export default class App extends React.Component{

  state = {
    searchInput: '',
    charities: [],
    donations: [],
    currentUser: {},
    userProfile: false,
    selectedCharity: {},
    charityCard: false,
    loginPage: true
}

  componentDidMount(){
    this.fetchAllCharities()
    this.fetchAllDonations()
    this.currentUser()
  }

  fetchAllCharities = () => {
    fetch(charityAPI)
    .then(res=>res.json())
    .then(charities => this.setState({ charities })
    ) 
  }

  fetchAllDonations = () => {
    fetch(donationAPI)
    .then(res=>res.json())
    .then(donations => this.setState({ donations })
    ) 
  }

  addNewUser = (newUser) => {
    this.setState({
      users: [...this.state.users, newUser],
      currentUser: newUser
    })
  }

  currentUser = () => {
    fetch(`${userAPI}/76`)
    .then(res => res.json())
    .then(currentUser => this.setState({ currentUser }))
  }

  showLoginPage = e => {
    if(this.state.loginPage === false){
        this.setState({
            loginPage: true
        })
    }else{
        this.setState({
        loginPage: false
        })
    }
  }

