//       || charity.cause.toLowerCase().includes(input.toLowerCase())
//       || charity.mission.toLowerCase().includes(input.toLowerCase())
// )}

// showCharityCard = (id) => {
//     const findCharity = this.props.charities.find(charity => charity.id === id)
//     if (this.state.charityCard === false){
//         this.setState({
//             selectedCharity: findCharity,
//             charityCard: true
//         })
//     }
// }

// hideCharityCard = e => {
//     this.setState({
//         charityCard: false
//     })
// }

  showUserProfile = () => {
    if(this.state.userProfile === false){
      this.setState({
          userProfile: true
      })
    }else{
      this.setState({
        userProfile: false
      })
    }
  }

  render(){
    return (
      <div className="App">
        <Nav currentUser={this.state.currentUser} userProfile={this.showUserProfile} />
        <div id="homepage_wrapper">
          {/* <HomePage charities={this.state.charities} currentUser={this.state.currentUser} donations={this.state.donations} userProfile={this.state.userProfile} onClick={this.showUserProfile}/> */}
          <Switch>
            <Route path='/search'  render={() =>  <HomePage  charities={this.state.charities} currentUser={this.state.currentUser} donations={this.state.donations} userProfile={this.state.userProfile} onClick={this.showUserProfile}/>} />
            <Route path='/profile'  render={() =>  <UserProfile userProfile={this.props.userProfile} currentUser={this.props.currentUser} charities={this.props.charities} donations={this.props.donations}  onClick={this.props.onClick}/>} />
            <Route path='/' render={() => <LoginPage loginPage={this.showLoginPage} display={this.state.loginPage}/>} />
          </Switch>
        </div>
      </div>
    );
  }
}
