import React from 'react'

export default class DonationBar extends React.Component {

    state = {
        widthPercentage: ''
    }

    calcBarWidth = () => {
        const maxAmount = 2000000
        const donation = this.props.donation_amount
        const percentage = donation/maxAmount
        return this.setState({
            widthPercentage: percentage
        })
    }

    render(){
        return(
            <div id="donationBar" style={{width: this.calcBarWidth}}>
                ${this.props.donation_amount}
            </div>
        )
    }
}
