import React from 'react';
import { HomePage, Nav, SignupForm} from './components';
import { Route, Switch } from 'react-router-dom';
import './styles/App.css';

const API = 'http://localhost:3000/charities'

export default class App extends React.Component{

  state = {
    charities: [],
    users: []
  }

  componentDidMount(){
    this.fetchAllCharities()
  }

  fetchAllCharities = () => {
    fetch(API)
    .then(res=>res.json())
    .then(charities => this.setState({ charities })
    ) 
  }

  addNewUser = (newUser) => {
    this.setState({
      authors: [...this.state.users, newUser],
      currentUser: newUser
    })
  }


  render(){
    return (
      <div className="App">
        <Nav  />
        <Switch>
          <Route path='/signup' render={(routerProps) => <SignupForm addNewUser={this.addNewUser} {...routerProps} />} />
          {/* <Route path='/users/:id' component={CharitieshowPage} />
          <Route path='/users' render={() => <UsersPage authors={this.state.authors} Charities={this.state.Charities} />} />  */}
          <Route path='/'  render={() =>  <HomePage charities={this.state.charities} />} />
        </Switch>
      </div>
    );
  }
}
