import React from 'react'
import SearchCard from './SearchCard'
import '../styles/CharitiesPage.css';

const favoritesAPI = 'http://localhost:3000/favorites'
export default class CharitiesPage extends React.Component{
    constructor(props){
        super(props)
        this.goBack = this.goBack.bind(this)
    }

    state = {
        favorites: [],
        userFavorites: []
    }

    componentDidMount(){
        this.fetchAllFavorites()
    }    

    fetchAllFavorites = () => {
        fetch(favoritesAPI)
        .then(res=>res.json())
        .then(favorites => this.setState({ favorites })
        ) 
    }

    goBack(){
        this.props.history.goBack();
    }

    userFavorites = () => {
        const faves = this.state.favorites.filter(favorites => favorites.user_id === this.props.currentUser.id)
        return faves 
    }

    userCharities = () => {
        if(!this.props.charities) return <div>Loading...</div>
        const FavCharities = this.props.charities.map(charity => charity.id === this.userFavorites().charity_id)
        return FavCharities
    }

    render(){
        console.log(this.userFavorites())
        return(
            <div id="charitiesPage">
                <div className="xClose xCharitiesPage" onClick={this.goBack}>x</div>
                <h1>MY CHARITIES</h1>
                {this.userFavorites().map(favorites => <FavoritedCharityCard id={favorites.id} {...favorites} />)}
            </div>
        )
    }
}
