import React from 'react'
import MainMenu from './MainMenu'


export default class Nav extends React.Component{

    state={
        display: false
    }

    clickHandler = e => {
        if(this.state.display === true){
            this.setState({
                display: false
            })
        }else{
            this.setState({
                display: true
            })
        }
    }

    mouseOut = e => {
        if(this.state.display === true){
            this.setState({
                display: false
            })
        }else{
            this.setState({
                display: true
            })
        }
    }

    render(){
        return(
            <div id="Nav">
                <div>
                    <i id="ChanceLogo" className="icon" />
                </div>
                <div>
                    <a className="dropdownMenu" onClick={this.clickHandler} href="#">☰MENU☰</a>
                </div>
                <div class="middle">
                    <MainMenu onMouseLeave={this.mouseOut} display={this.state.display} />
                </div>
            </div>
        )
    }
}
