import React from 'react'
import LoginCard from './LoginCard'
import SignupForm from './SignupForm'
import '../styles/Login.css';


export default class LoginPage extends React.Component{

    state = {
        displayLoginPage: true,
        signupForm: false
    }

    exitLogin = e => {
        this.setState({
            displayLoginPage: false
        })
    }

    showSignup = e => {
        if(this.state.signupForm === false){
            this.setState({
                signupForm: true
            })
        }else{
            this.setState({
                signupForm: false
            })
        }
    }

    render(){
        return(
            <div id="loginPage" style={{display: this.state.displayLoginPage ? "inline-block" : "none"}}>
                <h1 className="login-header">WELCOME TO THE <span className="chance-text">CHANCE</span> APP</h1>
                <h2 className="login-header">THE PHILANTHROPIC SOCIAL NETWORK</h2>
                <p className="login-signup-text">If you are a returning member, please log in. <br/>
                    New to Chance? <span className="signup-link" onClick={this.showSignup}>Create a new account</span>!
                </p>
                <p>Not interested in a membership? That's okay with us! The Chance app is free to use as a charitable organization directory.<br/>
                    If you wish to continue as a guest, <span className="signup-link" onClick={this.exitLogin}>click here</span>.
                </p>
                <LoginCard />
                <SignupForm display={this.state.signupForm} onClick={this.showSignup}/>
            </div>
        )
    }
}
