import React from 'react'
import '../styles/Inbox.css'

export default class Inbox extends React.Component{
    constructor(props){
        super(props)
        this.goBack = this.goBack.bind(this)
    }

    state = {
        msgWindow: false
    }

    goBack(){
        this.props.history.goBack();
    }

    showMsgWindow = e => {
        if(this.state.msgWindow){
            this.setState({
                msgWindow: false
            })
        }else{
            this.setState({
                msgWindow: true
            })
        }
    }

    render(){
        console.log(this.props.userInbox)
        if(!this.props.messages || !this.props.userInbox) return ''
        const messages = this.props.messages.filter(message => message.inbox_id === this.props.userInbox.id)
        return(
            <div id="inbox">
                <div className="xClose xInbox" onClick={() => { this.goBack()}}>x</div>
                <h1>MY MESSAGES</h1>
                <div id="inboxContainer">
                    <div className="inboxPanel">
                        {messages.map(message => <div>
                            <h3 className="msgTitle">{message.message_title}</h3>
                        </div>    
                        )}
                    </div>
                    <div className="messageWindow" style={{display: this.state.msgWindow ? "inline-block" : "none"}}>

                    </div>
                </div>
            </div>
        )
    }
}
