import React from 'react'
import CharityCard from './CharityCard'
import '../styles/CharitiesPage.css';

const favoritesAPI = 'http://localhost:3000/favorites'
export default class CharitiesPage extends React.Component{

    state = {
        favorites: []
    }

    componentDidMount(){
        this.fetchAllFavorites()
    }    

    fetchAllFavorites = () => {
        fetch(favoritesAPI)
        .then(res=>res.json())
        .then(favorites => this.setState({ favorites })
        ) 
    }

    userFavorites = () => {
        return this.state.favorites.filter(favorite => favorite.user_id === this.props.currentUser.id)
    }

    render(){
        return(
            <div id="charitiesPage">
                <div className="xClose xCharitiesPage" onClick={this.goBack}>x</div>
                <h1>MY CHARITIES</h1>
                {this.userFavorites().map(charity => <CharityCard/>)}
            </div>
        )
    }
}
