import React from 'react'

export default class Messages extends React.Component{

    state = {
        senders: [],
        msgWindow: false
    }

    showMsgWindow = e => {
        if(this.state.msgWindow){
            this.setState({
                msgWindow: false
            })
        }else{
            this.setState({
                msgWindow: true
            })
        }
    }

    msgSender = () =>{
        const sender = this.props.map(messages => messages.message_title)
        return sender
        // this.setState({
        //     senders
        // })
    } 

    render(){
        console.log(this.msgSender())
        // const groupMessages = this.props.filter(messages => messages.user_id === messages.user_id)
        return(
            <div id="messagesContainer">
                <div>
                    <h3 className="msgTitle" onClick={this.showMsgWindow}></h3>
                </div>
                <div className="messageWindow" style={{display: this.state.msgWindow ? "inline-block" : "none"}}>
                    hello world!
                </div>
            </div>
        )
    }
}
