import React from 'react'

export default class Messages extends React.Component{

    state = {
        msgWindow: false
    }

    showMsgWindow = e => {
        if(this.state.msgWindow){
            this.setState({
                msgWindow: false
            })
        }else{
            this.setState({
                msgWindow: true
            })
        }
    }

    render(){
        const groupMessages = this.props.filter(messages => messages.user_id === messages.user_id)
        const msgSender = this.props.allUsers.find(user => user.id === groupMessages.user_id)
        return(
            <div id="messagesContainer">
                <div>
                    <h3 className="msgTitle" onClick={this.showMsgWindow}>{msgSender.username}</h3>
                </div>
                <div className="messageWindow" style={{display: this.state.msgWindow ? "inline-block" : "none"}}>
                    hello world!
                </div>
            </div>
        )
    }
}
