import React from 'react'
import Messages from './Messages'
import '../styles/Inbox.css'

export default class Inbox extends React.Component{

    state = {
        messages: [],
        unique: []
    }

    constructor(props){
        super(props)
        this.goBack = this.goBack.bind(this)
    }

    goBack(){
        this.props.history.goBack();
    }

    uniqueSenders = () => {
            const sender = this.props.map(messages => messages.message_title)
            return sender
            // this.setState({
            //     senders
            // })
    }

    render(){
        console.log(this.uniqueSenders())
        if(!this.props.messages || !this.props.userInbox) return ''
        const messages = this.props.messages.filter(message => message.inbox_id === this.props.userInbox.id)
        return(
            <div id="inbox">
                <div className="xClose xInbox" onClick={() => { this.goBack()}}>x</div>
                <h1>MY MESSAGES</h1>
                <div id="inboxContainer">
                    <div className="inboxPanel">
                        {messages.map(messages => <Messages key={messages.id} {...messages} allUsers={this.props.allUsers} />   
                        )}
                    </div>
                </div>
            </div>
        )
    }
}
