import React from 'react'
// import useSound from 'use-sound'
import UIfx from 'uifx'
import heartSound from '../sounds/heartbeat.mp3'
import clickSound from '../sounds/click_5.mp3'

const beat = new UIfx({asset: heartSound})
export default class ChanceLogo extends React.Component{

  play = e => {
    beat.play()
  }

  render(){
    const click = new UIfx(
      {asset: clickSound}
      )



    return (
      <i id="ChanceLogo" className="icon" onMouseEnter={this.play} onClick={() => click()} />
    );
  }
}
