import React from 'react'

export default class DonationBar extends React.Component {

    // state = {
    //     bgColors: ['red', 'blue', 'yellow', 'yellowgreen', 'lightgreen', 'turquoise']
    // }

    calcBarWidth = () => {
        const maxAmount = 2000000
        const decimal = this.props.donation_amount/maxAmount
        const percentage = decimal*100
        return percentage.toFixed(2);
    }
    // randomColor = (bgColors) => {
    //     bgColors[Math.floor(Math.random()*bgColors.length)]
    // }

    render(){
        console.log(this.props)
        return(
            <div id="barContainer">
                <div className="donationBar" style={{width: `${this.calcBarWidth()}%`, backgroundColor: `${() => this.props.color(this.props.bgColors)}`}}>
                    ${this.props.donation_amount}
                    {this.calcBarWidth()}
                </div>
            </div>
        )
    }
}
