import React from 'react'
import SearchBar from './SearchBar'
import SearchResults from './SearchResults'
import '../styles/HomePage.css';

export default class HomePage extends React.Component{

    state = {
        searchInput: ''
    }

    searchCharities = () => {
        this.setState({
            [e.target.name]: e.target.value
          })
          this.displaySearchedCharities()      
    }

    displaySearchedCharities = e => {
        const allPosts = [...this.state.posts]
        const input = this.state.searchInput
        return allPosts.filter(posts => posts.title.toLowerCase().includes(input.toLowerCase())
          || posts.description.toLowerCase().includes(input.toLowerCase()))
        // || posts.author.toLowerCase().includes(input.toLowerCase()))
      }    

    render(){
        return(
            <div id="homepage_wrapper">
                <SearchBar onChange={this.searchCharities} value={this.state.searchInput}/>
                <SearchResults charities={this.displaySearchedCharities()} />
            </div>
        )
    }
}
