import React from 'react'
import '../styles/Inbox.css'

export default class Inbox extends React.Component{
    constructor(props){
        super(props)
        this.goBack = this.goBack.bind(this)
    }

    goBack(){
        this.props.history.goBack();
    }

    render(){
        console.log(this.props.userInbox)
        if(!this.props.messages || !this.props.userInbox) return 'Loading Messages...'
        const messages = this.props.messages.filter(message => message.inbox_id === this.props.userInbox.id)
        return(
            <div id="inbox">
                <div className="xClose xInbox" onClick={() => { this.goBack()}}>x</div>
                <h1>MY MESSAGES</h1>
                <div id="inboxContainer">
                    <div className="inboxPanel">
                        {messages.map(message => <div>
                            {message.message_title}
                        </div>    
                        )}
                    </div>
                    <div className="messageWindow">

                    </div>
                </div>
            </div>
        )
    }
}
