import React from 'react'

const messageAPI = 'http://localhost:3000/messages'
export default class Messages extends React.Component{

    state = {
        // senders: [],
        msgWindow: false
    }

    showMsgWindow = e => {
        if(this.state.msgWindow){
            this.setState({
                msgWindow: false
            })
        }else{
            this.setState({
                msgWindow: true
            })
        }
    }

    submitMsg = e => {
        e.preventDefault()
        fetch(messageAPI, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                accept: 'application/json'
            },
            body: JSON.stringify({
                user_id: this.props.currentUser.id,
                inbox_id: this.props.messages[0].inbox_id
            })
        })
        .then(res => res.json())
        .then(message => this.props.postMsg(message))
    }


    render(){
        const messages = this.props.messages
            .sort((a,b) => a.id - b.id)
            .filter(messages => messages.message_title === this.props.title)
        return(
            <div id="messagesContainer">
                <div>
                    <h2 className="msgTitle" onClick={this.showMsgWindow}>{this.props.title}</h2>
                </div>
                <div className="messageWindow msgDropdown" style={{display: this.state.msgWindow ? "inline-block" : "none"}}>
                    <div className="messageInput">
                        <form>
                            <table width="100%" border="0" cellPadding="0" cellSpacing="0" align="center">
                                <tbody>
                                    <tr>
                                        <td width="100%" align="left"><input className="msgInput" type="text"></input></td>
                                        <td className="sendBtn" align="left">SEND</td>
                                    </tr>
                                </tbody>
                            </table>
                        </form>
                    </div>
                    {messages.sort((a,b) => a.id - b.id).map(msgBody => 
                    <div className="msgBody">
                        <h3>{msgBody.user.username}</h3>
                        <p>{msgBody.message_body}</p>
                    </div>
                    )}
                </div>
            </div>
        )
    }
}
