import React from 'react'

export default class Messages extends React.Component{

    state = {
        // senders: [],
        msgWindow: false
    }

    showMsgWindow = e => {
        if(this.state.msgWindow){
            this.setState({
                msgWindow: false
            })
        }else{
            this.setState({
                msgWindow: true
            })
        }
    }



    render(){
        const messages = this.props.messages.sort((a,b) => a.id - b.id).filter(messages => messages.message_title === this.props.title)
        // const sender = messages.
        return(
            <div id="messagesContainer">
                <div>
                    <h3 className="msgTitle" onClick={this.showMsgWindow}>{this.props.title}</h3>
                </div>
                <div className="messageWindow" style={{display: this.state.msgWindow ? "inline-block" : "none"}}>
                    {messages.sort((a,b) => b.id - a.id).map(msgBody => <div>
                        <h3>{msgBody.user.username}</h3>
                        <div>{msgBody.message_body}</div>
                    </div>
                    )}
                </div>
            </div>
        )
    }
}
